import React, { Component } from 'react';

const USER_SERVICE_URL = 'https://api.github.com/users/Kenshikento/repos'; // github url

class Portfolio extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            isFetching: false,
            repo: [] 
        };
    }

    componentDidMount() {
        console.log(this.fetchUsers());
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    fetchUsersWithFetchAPI = () => {
        this.setState({...this.state, isFetching: true});
        console.log(123);
        fetch(USER_SERVICE_URL)
            .then(response => response.json())
            .then(result => {
                this.setState({repo: result, isFetching: false})
            })
            .catch(e => {
                console.log(e);
                this.setState({...this.state, isFetching: false});
            });
    };

    fetchUsers = this.fetchUsersWithFetchAPI

    render() {
        return (
            <ul>
                <h4>Repos</h4>
             
                <br />
                <br />
                <h3>List of repositories</h3>
              
    
            </ul>
        )
    }
}

export default Portfolio;
