import React, { Component } from 'react';
import RepoList from './RepoList';
import './Portfolio.scss';

const USER_SERVICE_URL = 'https://api.github.com/users/Kenshikento/repos';

class Portfolio extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            isFetching: false,
            repo: [] 
        };
    }

    componentDidMount() {
        this.fetchUsers();
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    fetchUsersWithFetchAPI = () => {
        this.setState({...this.state, isFetching: true});

        fetch(USER_SERVICE_URL)
            .then(response => response.json())
            .then(result => {
                let groupLang = this.groupBy(result,'language');
                this.setState({repo: groupLang, isFetching: false})
            })
            .catch(e => {
                console.log(e); // TODO: to remove
                this.setState({...this.state, isFetching: false});
            });
    };

    fetchUsers = this.fetchUsersWithFetchAPI;
    
    groupBy = (array, key) => {
        return array.reduce((result, currentValue) => {
          (result[currentValue[key]] = result[currentValue[key]] || []).push(
            currentValue
          );
          return result;
        }, {});
    };

    toggle =  (name) => {
        console.log(name);
    }

    render() {
        //const groupLang = this.groupBy(this.state.repo,'language');
        console.log(this.repo)
        return (
            <ul>
                <h1>List of GitHub repositories</h1>
            </ul>
        )
    }
}

export default Portfolio;
