import React, { Component } from 'react';

const USER_SERVICE_URL = 'https://api.github.com/users/Kenshikento/repos';

class Portfolio extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            isFetching: false,
            repo: [] 
        };
    }

    componentDidMount() {
        this.fetchUsers();
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    fetchUsersWithFetchAPI = () => {
        this.setState({...this.state, isFetching: true});

        fetch(USER_SERVICE_URL)
            .then(response => response.json())
            .then(result => {
                this.setState({repo: result, isFetching: false})
                //console.log(result);
            })
            .catch(e => {
                console.log(e);
                this.setState({...this.state, isFetching: false});
            });
    };

    fetchUsers = this.fetchUsersWithFetchAPI;
    
    groupBy = (array, key) => {
        // Return the end result
        return array.reduce((result, currentValue) => {
          // If an array already present for key, push it to the array. Else create an array and push the object
          (result[currentValue[key]] = result[currentValue[key]] || []).push(
            currentValue
          );
          // Return the current iteration `result` value, this will be taken as next iteration `result` value and accumulate
          return result;
        }, {}); // empty object is the initial value for result object
    };

    render() {
        const grouplang = this.groupBy(this.state.repo,'language');
        //console.log(grouplang);
        /*const items = this.state.repo.map(
            (item) =>
                    // need to add in my own component below this 
                    <li key={item.id}><a href ={item.svn_url}>{item.name} {item.language}</a></li>
            )
        ;*/

        return (
            <ul>
                <h4>Repos</h4>
                <h3>List of repositories</h3>
                {Object.entries(grouplang).map((item, i) => (
                    console.log(item,i)
                ))}
            </ul>
        )
    }
}

export default Portfolio;
