import React, { Component } from 'react';
import RepoList from './RepoList';
import './Portfolio.scss';

const USER_SERVICE_URL = 'https://api.github.com/users/Kenshikento/repos';

class Portfolio extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            isFetching: false,
            repo: [],
            key: [],
        };

        this.toggle = this.toggle.bind(this);
    }

    componentDidMount() {
        this.fetchUsers();
    }

    componentWillUnmount() {
        clearInterval(this.timer);
        this.timer = null;
    }

    fetchUsersWithFetchAPI = () => {
        this.setState({...this.state, isFetching: true});

        fetch(USER_SERVICE_URL)
            .then(response => response.json())
            .then(result => {
                let groupLang = this.groupBy(result,'language');
                let keyz = [];
                for(const[key, value] of Object.entries(groupLang)) {
                    console.log(key);
                }

                let keys = Object.keys(groupLang);
                this.setState({repo: groupLang, isFetching: false, key:keys})
            })
            .catch(e => {
                console.log(e); // TODO: to remove
                this.setState({...this.state, isFetching: false});
            });
    };

    fetchUsers = this.fetchUsersWithFetchAPI;
    
    groupBy = (array, key) => {
        return array.reduce((result, currentValue) => {
          (result[currentValue[key]] = result[currentValue[key]] || []).push(
            currentValue
          );
          return result;
        }, {});
    };

    toggle =  (e, name) => {
        console.log(name);
    }

    render() {
        //const groupLang = this.groupBy(this.state.repo,'language');

        return (
            <ul>
                <h1>List of GitHub repositories</h1>
                {Object.entries(this.state.repo).map((item) => { 
                    let name = item[0];
                    let list = item[1];
                    
                    return (
                        <div>
                            <div className="title" onClick={(e)=> this.toggle(e,name)}> 
                                <h1> {name} </h1>
                            </div>
                            { list.map((repo) => {                            
                                return (
                                    <RepoList
                                        name={repo.name}
                                        link={repo.svn_url}
                                        title={name}
                                    />
                                )
                            })}
                        </div>
                    )
                })}                
            </ul>
        )
    }
}

export default Portfolio;
